import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Typography, Box, Avatar, Button, TextField, Grid, Paper, IconButton } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import SaveIcon from '@mui/icons-material/Save';
import HomeIcon from '@mui/icons-material/Home';
import SettingsIcon from '@mui/icons-material/Settings';
import AddIcon from '@mui/icons-material/Add';
import { getAuth } from "firebase/auth";
import { storage } from '../firebase';
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from '../auth/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';

const ProfilePage = () => {
    const [userDetails, setUserDetails] = useState({});
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({});
    const [selectedFile, setSelectedFile] = useState(null);
    const { user } = useAuth();
    const navigate = useNavigate();
    const { id } = useParams();
    const auth = getAuth();
    const userId = id ? id : user?._id;

    useEffect(() => {
        if (!userId) return;

        const fetchUserProfile = async () => {
            try {
                const res = await axios.get(`http://localhost:5001/api/users/profile/${userId}`);
                setUserDetails(res.data);
                setFormData(res.data);
            } catch (err) {
                console.error("Error fetching profile:", err.message);
            }
        };

        fetchUserProfile();
    }, [userId]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleImageUpload = async () => {
        if (!selectedFile) return alert("Please select an image first!");

        const storageRef = ref(storage, `profilePictures/${userId}/${selectedFile.name}`);
        const uploadTask = uploadBytesResumable(storageRef, selectedFile);

        uploadTask.on(
            "state_changed",
            null,
            (error) => alert("Upload failed! Check Firebase permissions."),
            async () => {
                const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
                try {
                    const res = await axios.put(`http://localhost:5001/api/users/profile/${userId}`, {
                        profileImage: downloadURL
                    });
                    setUserDetails(res.data.user);
                    setFormData({ ...formData, profileImage: downloadURL });
                    alert("Profile picture updated successfully!");
                } catch (err) {
                    console.error("Error updating profile picture:", err.message);
                }
            }
        );
    };

    const handleSave = async () => {
        try {
            const updatedData = { ...formData, profileImage: userDetails.profileImage };
            const res = await axios.put(`http://localhost:5001/api/users/profile/${userId}`, updatedData);
            setUserDetails(res.data.user);
            setEditMode(false);
            alert('Profile updated successfully!');
        } catch (err) {
            console.error("Error updating profile:", err.message);
        }
    };

    const handleHomeClick = () => {
        navigate('/userhome');
    };

    return (
        <Container maxWidth="xs" sx={{ width: 600, height: 800, bgcolor: 'white', borderRadius: 3, position: 'relative', overflow: 'hidden', p: 2, border: '2px solid #ccc' }}>
            <Box display="flex" justifyContent="flex-end">
                <IconButton onClick={() => setEditMode(!editMode)} color="primary">
                    <SettingsIcon />
                </IconButton>
            </Box>

            <Box display="flex" flexDirection="column" alignItems="center">
                <label htmlFor="file-input">
                    <Avatar
                        src={formData.profileImage || "https://upload.wikimedia.org/wikipedia/commons/8/89/Portrait_Placeholder.png"}
                        sx={{ width: 100, height: 100, mb: 2, cursor: editMode ? 'pointer' : 'default' }}
                    />
                </label>
                {editMode && (
                    <>
                        <input
                            id="file-input"
                            type="file"
                            style={{ display: 'none' }}
                            onChange={handleFileChange}
                        />
                        <Button
                            variant="contained"
                            color="secondary"
                            size="small"
                            sx={{ mt: 1, textTransform: 'none', borderRadius: 2 }}
                            onClick={handleImageUpload}
                        >
                            Upload Image
                        </Button>
                    </>
                )}

                {editMode ? (
                    <TextField
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        label="Full Name"
                        variant="outlined"
                        fullWidth
                        sx={{ mb: 2 }}
                    />
                ) : (
                    <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
                        {userDetails.name || 'Your Name'}
                    </Typography>
                )}

                {editMode ? (
                    <TextField
                        name="bio"
                        value={formData.bio}
                        onChange={handleChange}
                        label="Bio"
                        variant="outlined"
                        fullWidth
                        multiline
                        rows={2}
                        sx={{ mb: 2 }}
                    />
                ) : (
                    <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                        {userDetails.bio || ''}
                    </Typography>
                )}

                {editMode && (
                    <Button
                        variant="contained"
                        color="primary"
                        startIcon={<SaveIcon />}
                        sx={{ mt: 2, textTransform: 'none', borderRadius: 2 }}
                        onClick={handleSave}
                    >
                        Save Changes
                    </Button>
                )}
            </Box>
        </Container>
    );
};

export default ProfilePage;
