import React, { useState, useEffect } from 'react';
import { Typography, Button, Container, Paper, TextField, Avatar, Box, IconButton, MenuItem, Menu } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import axios from 'axios';
import AddIcon from '@mui/icons-material/Add';

const UserHome = () => {
    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null); // Anchor for dropdown
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const res = await axios.get('http://localhost:5001/api/posts/all');
                // Transform the data
                const transformedPosts = res.data.map(post => ({
                    content: post.content,
                    createdAt: new Date(post.createdAt).toLocaleString(),
                    dislikes: post.dislikes,
                    likes: post.likes,
                    postimg: post.postimg,
                    userId: post.userId,
                    userName: post.userName,
                    postId: post._id,
                }));
                setPosts(transformedPosts);
            } catch (err) {
                console.error('Error fetching posts:', err);
            }
        };

        const fetchUsers = async () => {
            try {
                const res = await axios.get('http://localhost:5001/api/users/all');
                const transformedUsers = res.data.map(user => ({
                    id: user._id,
                    name: user.name,
                    bio: user.bio,
                    email: user.email,
                    role: user.role,
                    university: user.university,
                    profileImage: user.profileImage,
                    joinedAt: new Date(user.joinedAt).toLocaleDateString(),
                }));
                setUsers(transformedUsers);
                setFilteredUsers(transformedUsers);
            } catch (err) {
                console.error('Error fetching users:', err);
            }
        };

        fetchPosts();
        fetchUsers();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        setAnchorEl(value ? e.currentTarget : null); // Show dropdown when typing starts

        setFilteredUsers(
            value
                ? users.filter(
                      (u) =>
                          u.name.toLowerCase().startsWith(value.toLowerCase()) ||
                          u.email.toLowerCase().startsWith(value.toLowerCase())
                  )
                : users
        );
    };

    const handleProfile = (userId) => {
        navigate(`/profile/${userId}`);
    };

    return (
        <Container
            maxWidth="xs"
            sx={{
                width: 600,
                height: 800,
                position: 'relative',
                overflow: 'hidden',
                bgcolor: 'white',
                borderRadius: 3,
                p: 2,
                border: '2px solid #000',
                display: 'flex',
                flexDirection: 'column',
            }}
        >
            <Typography
                variant="h5"
                sx={{
                    textAlign: 'center',
                    fontWeight: 'bold',
                    color: 'black',
                    mt: 1,
                }}
            >
                PaceX
            </Typography>

            {/* Stories Section */}
            <Box
                sx={{
                    display: 'flex',
                    overflowX: 'auto',
                    gap: 1,
                    pb: 1,
                    scrollbarWidth: 'none',
                    '&::-webkit-scrollbar': { display: 'none' },
                    cursor: 'grab',
                }}
            >
                {users.slice(0, 10).map((user) => (
                    <Avatar
                        key={user.id}
                        sx={{ width: 60, height: 60, cursor: 'pointer', border: '3px solid #ff4500' }}
                        onClick={() => handleProfile(user.id)}
                        src={user.profileImage}
                    />
                ))}
            </Box>

            {/* Search Users */}
            <TextField
                fullWidth
                placeholder="Search users..."
                value={searchTerm}
                onChange={handleSearchChange}
                sx={{ mt: 2, bgcolor: 'white', borderRadius: 1 }}
            />

            {/* Dropdown for filtered users */}
            <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={() => setAnchorEl(null)}
                sx={{
                    maxHeight: 200,
                    overflowY: 'auto',
                    bgcolor: 'white', // White background for the dropdown
                    mt: 1, // Margin top to give space between search field and dropdown
                }}
            >
                {filteredUsers.map((user) => (
                    <MenuItem key={user.id} onClick={() => handleProfile(user.id)}>
                        {user.name} ({user.email})
                    </MenuItem>
                ))}
            </Menu>

            <Box
                sx={{
                    mt: 3, // Added margin to prevent overlap with dropdown
                    height: 500,
                    overflowY: 'auto',
                    '&::-webkit-scrollbar': {
                        display: 'none',
                    },
                    scrollbarWidth: 'none',
                }}
            >
                {posts.map((post, index) => {
                    const postUser = users.find((user) => user.id === post.userId);
                    return (
                        <Paper key={index} sx={{ p: 2, mb: 2, backgroundColor: 'transparent' }}>
                            {postUser && (
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    <Avatar
                                        src={postUser.profileImage}
                                        sx={{ width: 40, height: 40 }}
                                        onClick={() => handleProfile(postUser.id)}
                                    />
                                    <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                                        {postUser.name}
                                    </Typography>
                                </Box>
                            )}
                            {post.postimg && (
                                <Box sx={{ mt: 2, maxHeight: 400, overflow: 'hidden' }}>
                                    <img src={post.postimg} alt="Post" style={{ width: '100%', height: 'auto' }} />
                                </Box>
                            )}
                            {post.content && (
                                <Typography sx={{ wordWrap: 'break-word', mt: 2 }}>
                                    {post.content}
                                </Typography>
                            )}
                            <Box sx={{ display: 'flex', gap: 2, mt: 2, alignItems: 'center' }}>
                                <Typography sx={{ fontWeight: 'bold' }}>
                                    Likes: {post.likes}
                                </Typography>
                                <Typography sx={{ fontWeight: 'bold' }}>
                                    Dislikes: {post.dislikes}
                                </Typography>
                            </Box>
                        </Paper>
                    );
                })}
            </Box>

            <Box
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    gap: 2,
                    position: 'absolute',
                    bottom: 10,
                    left: '50%',
                    transform: 'translateX(-50%)',
                }}
            >
                <IconButton onClick={() => handleProfile(user._id)}>
                    <Avatar src={user?.profilePic} sx={{ width: 50, height: 50 }} />
                </IconButton>

                <IconButton sx={{ width: 50, height: 50, bgcolor: '#ff4500', color: 'white', borderRadius: '50%' }}>
                    <AddIcon />
                </IconButton>
            </Box>
        </Container>
    );
};

export default UserHome;
