import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  Avatar, 
  Typography, 
  Paper, 
  Button, 
  TextField, 
  Grid, 
  IconButton, 
  Modal 
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import SaveIcon from '@mui/icons-material/Save';
import HomeIcon from '@mui/icons-material/Home';
import SettingsIcon from '@mui/icons-material/Settings';
import AddIcon from '@mui/icons-material/Add';
import LogoutIcon from '@mui/icons-material/Logout';
import { getAuth } from "firebase/auth";
import { storage } from '../firebase';
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from '../auth/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';
import { signOut } from "firebase/auth";
import axios from 'axios';
import CameraCapture from './CameraComponent';

const ProfilePage = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});
  const [selectedFile, setSelectedFile] = useState(null);
  const [openCamera, setOpenCamera] = useState(false);
  const { user } = useAuth();
  const navigate = useNavigate();
  const { id } = useParams();
  const auth = getAuth();
  const userId = id ? id : user?._id;

  useEffect(() => {
    if (!userId) return;
    const fetchUserProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:5001/api/users/profile/${userId}`);
        setUserDetails(res.data);
        setFormData(res.data);
      } catch (err) {
        console.error("Error fetching profile:", err.message);
      }
    };
    fetchUserProfile();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const res = await axios.put(`http://localhost:5001/api/users/profile/${userId}`, formData);
      if (res.data && res.data.user) {
        setUserDetails(res.data.user);
      }
      setEditMode(false);
      alert('Profile updated successfully!');
    } catch (err) {
      console.error("Error updating profile:", err.message);
    }
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleImageUpload = async () => {
    if (!selectedFile) return alert("Please select an image first!");
    const storageRef = ref(storage, `profilePictures/${userId}/${selectedFile.name}`);
    const uploadTask = uploadBytesResumable(storageRef, selectedFile);
    uploadTask.on(
      "state_changed",
      null,
      (error) => alert("Upload failed! Check Firebase permissions."),
      async () => {
        const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
        setFormData({ ...formData, profileImage: downloadURL });
      }
    );
  };

  // Callback for camera capture upload
  const handleCameraImageUpload = (downloadURL) => {
    setFormData({ ...formData, profileImage: downloadURL });
    setOpenCamera(false);
  };

  const handleHomeClick = () => {
    navigate('/userhome');
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/home');
    } catch (err) {
      console.error("Error signing out:", err.message);
    }
  };

  // **MODIFIED:** Added a handler for clicking the profile image (when not in edit mode) to view stories.
  const handleStoriesClick = () => {
    navigate(`/stories/${userId}`);
  };

  return (
    <>
      <Container
        sx={{
          width: 600,
          height: 800,
          margin: '50px auto',
          backgroundColor: '#fff',
          borderRadius: 3,
          position: 'relative',
          overflow: 'hidden',
          p: 2,
          border: '2px solid #ccc'
        }}
      >
        {/* Top Section: Logout & Settings */}
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <IconButton onClick={handleLogout} color="secondary">
            <LogoutIcon />
          </IconButton>
          <IconButton onClick={() => setEditMode(!editMode)} color="primary">
            <SettingsIcon />
          </IconButton>
        </Box>

        <Box display="flex" flexDirection="column" alignItems="center">
          {/* **MODIFIED:** Render profile image differently in edit vs. view mode */}
          {editMode ? (
            <label htmlFor="file-input">
              <Avatar
                src={formData.profileImage || "https://upload.wikimedia.org/wikipedia/commons/8/89/Portrait_Placeholder.png"}
                sx={{ width: 100, height: 100, mb: 2, cursor: 'pointer' }}
              />
            </label>
          ) : (
            <IconButton onClick={handleStoriesClick} sx={{ p: 0 }}>
              <Avatar
                src={formData.profileImage || "https://upload.wikimedia.org/wikipedia/commons/8/89/Portrait_Placeholder.png"}
                sx={{ width: 100, height: 100, mb: 2 }}
              />
            </IconButton>
          )}
          {editMode && (
            <>
              <input
                id="file-input"
                type="file"
                style={{ display: 'none' }}
                onChange={handleFileChange}
              />
              <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                <Button
                  variant="contained"
                  color="secondary"
                  size="small"
                  sx={{ textTransform: 'none', borderRadius: 2 }}
                  onClick={handleImageUpload}
                >
                  Upload Image
                </Button>
                <Button
                  variant="outlined"
                  color="primary"
                  size="small"
                  sx={{ textTransform: 'none', borderRadius: 2 }}
                  onClick={() => setOpenCamera(true)}
                >
                  Open Camera
                </Button>
              </Box>
            </>
          )}

          {/* Profile Info / Settings Form */}
          {editMode ? (
            <Paper
              sx={{
                p: 3,
                mt: 2,
                backgroundColor: '#e3f2fd',
                borderRadius: 2,
                boxShadow: 3,
                width: '100%'
              }}
            >
              <Typography variant="h6" sx={{ mb: 2, fontWeight: 'bold', textAlign: 'center' }}>
                Edit Profile
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <TextField
                  name="username"
                  value={formData.username || ''}
                  onChange={handleChange}
                  label="Username"
                  variant="outlined"
                  fullWidth
                />
                <TextField
                  name="name"
                  value={formData.name || ''}
                  onChange={handleChange}
                  label="Full Name"
                  variant="outlined"
                  fullWidth
                />
                <TextField
                  name="bio"
                  value={formData.bio || ''}
                  onChange={handleChange}
                  label="Bio"
                  variant="outlined"
                  fullWidth
                  multiline
                  rows={2}
                />
                <Button
                  variant="contained"
                  color="primary"
                  sx={{ mt: 2, textTransform: 'none', borderRadius: 2, width: '100%' }}
                  onClick={handleSave}
                >
                  Save Changes
                </Button>
              </Box>
            </Paper>
          ) : (
            <>
              <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                {userDetails.username || ''}
              </Typography>
              <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
                {userDetails.name || 'Your Name'}
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                {userDetails.bio || ''}
              </Typography>
            </>
          )}
        </Box>

        {/* User Stats */}
        <Box sx={{ display: 'flex', gap: 3, mt: 3, justifyContent: 'center' }}>
          <Typography variant="body2">Posts: {userDetails.posts?.length || 0}</Typography>
          <Typography variant="body2">Followers: {userDetails.followers?.length || 0}</Typography>
          <Typography variant="body2">Following: {userDetails.following?.length || 0}</Typography>
        </Box>

        {/* Posts Grid */}
        <Box sx={{ mt: 5, pt: 3, px: 2 }}>
          {userDetails.posts && userDetails.posts.length > 0 ? (
            <Grid container spacing={3}>
              {userDetails.posts.map((post, index) => (
                <Grid item xs={12} sm={6} md={4} key={index}>
                  <Paper sx={{ p: 3, backgroundColor: 'transparent', borderRadius: 2 }}>
                    <Typography variant="body2">{post.content}</Typography>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Typography variant="body2" sx={{ textAlign: 'center', color: 'gray', fontStyle: 'italic' }}>
              No Posts Available
            </Typography>
          )}
        </Box>

        {/* Bottom Navigation: Home and Profile */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'center',
            gap: 20,
            position: 'absolute',
            bottom: 10,
            left: '50%',
            transform: 'translateX(-50%)'
          }}
        >
          {/* Home Button */}
          <IconButton 
            onClick={handleHomeClick} 
            sx={{ 
              backgroundColor: '#ff9800', 
              color: 'white', 
              borderRadius: '50%', 
              width: 50,             
              height: 50,            
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <HomeIcon fontSize="large" />
          </IconButton>
          </Box>
          </Container>

      {/* Camera Modal */}
      <Modal open={openCamera} onClose={() => setOpenCamera(false)}>
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 500,
            height: 700,
            bgcolor: 'white',
            p: 2,
            borderRadius: 2,
            boxShadow: 24,
          }}
        >
          <CameraCapture 
            userId={userId} 
            onImageUpload={handleCameraImageUpload} 
            onClose={() => setOpenCamera(false)} 
          />
        </Box>
      </Modal>
    </>
  );
};

export default ProfilePage;
