import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  Avatar, 
  Typography, 
  Paper, 
  Button, 
  TextField, 
  Grid,            // Added Grid import
  IconButton, 
  Modal, 
  Input, 
  List, 
  ListItem, 
  ListItemText 
} from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import SettingsIcon from '@mui/icons-material/Settings';
import LogoutIcon from '@mui/icons-material/Logout';
import AddIcon from '@mui/icons-material/Add';
import ChatIcon from '@mui/icons-material/Chat';
import CloseIcon from '@mui/icons-material/Close';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import { getAuth } from "firebase/auth";
import { storage } from '../firebase';
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from '../auth/AuthContext';
import { useNavigate, useParams } from 'react-router-dom';
import { signOut } from "firebase/auth";
import axios from 'axios';
import CameraCapture from './CameraComponent';

const UserHome = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [openCamera, setOpenStoryCamera] = useState(false);
  const [stories, setStories] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);
  const [openStory, setOpenStory] = useState(false);
  const [currentStories, setCurrentStories] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [storyUser, setstoryUser] = useState(null);
  const [currentIndexStory, setCurrentIndexStory] = useState(0);

  // NEW: Declare state for post modal
  const [openPostModal, setOpenPostModal] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  // Function to mark a story as viewed
  const markStoryAsViewed = async (storyId, userId) => {
    try {
      const postdata = { storyId, userId };
      console.log("postdata", postdata);
      await axios.put("http://localhost:5001/api/story/view", postdata);
      console.log(`ðŸ‘€ User ${user?._id} viewed story ${storyId}`);
    } catch (error) {
      console.error("ðŸ”¥ Error updating story view:", error);
    }
  };

  useEffect(() => {
    if (openStory && currentStories.length > 0) {
      console.log(currentStories[currentIndexStory]);
      markStoryAsViewed(currentStories[currentIndexStory].storyId, user?._id);
    }
  }, [currentIndexStory, openStory]);

  const handleNext = () => {
    if (currentIndexStory < currentStories.length - 1) {
      setCurrentIndexStory((prevIndex) => prevIndex + 1);
    } else {
      handleClose(); // Close modal if it's the last story
    }
  };

  const handlePrev = () => {
    if (currentIndexStory > 0) {
      setCurrentIndexStory((prevIndex) => prevIndex - 1);
    }
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "ArrowRight") handleNext();
      if (event.key === "ArrowLeft") handlePrev();
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [currentIndex]);

  const handleStoryClick = (storyUser, stories) => {
    console.log("storyUser", storyUser);
    setstoryUser(storyUser);
    setCurrentStories(stories);
    setCurrentIndex(0);
    setOpenStory(true);
  };

  const handleClose = () => {
    setOpenStory(false);
    setCurrentStories([]);
    setCurrentIndexStory(0);
  };

  const handleImageUpload = async (downloadURL) => {
    try {
      console.log("downloadurl", downloadURL);
      const postData = {
        userId: user?._id,
        userName: user?.name,
        mediaUrl: downloadURL,
        mediaType: 'image'
      };
      console.log('Post Data:', postData);
      await axios.post('http://localhost:5001/api/story/add', postData);
      fetchStories();
      console.log("âœ… Story uploaded successfully:", downloadURL);
      setOpenStoryCamera(false);
    } catch (err) {
      console.error('Error creating post:', err.response?.data || err.message);
    }
  };

  const groupedStories = stories.reduce((acc, story) => {
    if (!acc[story.userId]) {
      acc[story.userId] = { userId: story.userId, stories: [] };
    }
    acc[story.userId].stories.push(story);
    return acc;
  }, {});
  const uniqueUserStories = Object.values(groupedStories);

  const fetchStories = async () => {
    try {
      const res = await axios.get('http://localhost:5001/api/story/all');
      const todaystories = res.data.map(story => ({
        storyId: story._id,
        userId: story.userId,
        userName: story.userName,
        mediaUrl: story.mediaUrl,
        mediaType: story.mediaType,
        views: story.views
      }));
      console.log("story", todaystories);
      setStories(todaystories);
    }
    catch (err) {
      console.error('Error fetching story:', err);
    }
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await axios.get('http://localhost:5001/api/posts/all');
        const transformedPosts = res.data.map(post => ({
          content: post.content,
          createdAt: new Date(post.createdAt).toLocaleString(),
          dislikes: post.dislikes,
          likes: post.likes,
          postimg: post.postimg,
          userId: post.userId,
          userName: post.userName,
          postId: post._id,
        }));
        setPosts(transformedPosts);
        console.log(transformedPosts);
      } catch (err) {
        console.error('Error fetching posts:', err);
      }
    };

    const fetchUsers = async () => {
      try {
        const res = await axios.get('http://localhost:5001/api/users/all');
        const transformedUsers = res.data.map(user => ({
          id: user._id,
          name: user.name,
          bio: user.bio,
          email: user.email,
          role: user.role,
          university: user.university,
          profileImage: user.profileImage,
          joinedAt: new Date(user.joinedAt).toLocaleDateString(),
        }));
        console.log(transformedUsers);
        setUsers(transformedUsers);
        setFilteredUsers(transformedUsers);
      } catch (err) {
        console.error('Error fetching users:', err);
      }
    };

    fetchPosts();
    fetchUsers();
    fetchStories();
  }, []);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    setFilteredUsers(
      value
        ? users.filter((u) =>
            u.name.toLowerCase().startsWith(value.toLowerCase()) ||
            u.email.toLowerCase().startsWith(value.toLowerCase())
          )
        : users
    );
  };

  const handleProfile = (userId) => {
    navigate(`/profile/${userId}`);
  };
  const handleAddpost = () => {
    navigate(`/add-post`);
  };

  const handleLikeToggle = (postId) => {
    setLikedPosts((prev) => ({
      ...prev,
      [postId]: !prev[postId],
    }));
  };

  // When a post is clicked, open a modal to show its details
  const handlePostClick = (post) => {
    setSelectedPost(post);
    setOpenPostModal(true);
  };

  return (
    <>
      <Container
        sx={{
          width: 600,
          height: 800,
          margin: '50px auto',
          border: '2px solid #000',
          borderRadius: 3,
          overflow: 'hidden',
          display: 'flex',
          p: 0,
          backgroundColor: 'white'
        }}
      >
        {/* Left Sidebar for Community */}
        <Box
          sx={{
            width: 50, // **CHANGED:** Fixed width to 150px
            flexShrink: 0,
            borderRight: '1px solid #ccc',
            overflowY: 'auto',
            p: 2,
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 2 }}>
            Community
          </Typography>
          <List>
            {/* Dummy community data */}
            <ListItem button>
              <ListItemText primary="Community 1" secondary="Description" />
            </ListItem>
            <ListItem button>
              <ListItemText primary="Community 2" secondary="Description" />
            </ListItem>
            <ListItem button>
              <ListItemText primary="Community 3" secondary="Description" />
            </ListItem>
          </List>
        </Box>

        {/* Main Content */}
        <Box sx={{ width: '75%', position: 'relative', display: 'flex', flexDirection: 'column', p: 2 }}>
          {/* Top Bar with App Name */}
          <Typography
            variant="h5"
            sx={{
              textAlign: 'center',
              fontWeight: 'bold',
              color: 'black',
              mb: 2
            }}
          >
            PaceX
          </Typography>

          {/* Improved Search Bar */}
          <TextField
            fullWidth
            placeholder="Search users..."
            value={searchTerm}
            onChange={handleSearchChange}
            sx={{
              mb: 2,
              '& .MuiOutlinedInput-root': {
                borderRadius: '30px',
                backgroundColor: '#f5f5f5'
              }
            }}
          />
          {searchTerm && (
            <Box
              sx={{
                maxHeight: '200px',
                overflowY: 'auto',
                border: '1px solid #ccc',
                borderRadius: '4px',
                mb: 2,
                position: 'absolute',
                width: 'calc(75% - 32px)',
                zIndex: 100,
                backgroundColor: 'white'
              }}
            >
              <List>
                {filteredUsers.slice(0, 5).map((user) => (
                  <ListItem button key={user.id} onClick={() => handleProfile(user.id)}>
                    <Avatar
                      src={user.profileImage}
                      sx={{ width: 30, height: 30, mr: 2 }}
                    />
                    <ListItemText primary={`${user.name} - ${user.email}`} />
                  </ListItem>
                ))}
              </List>
            </Box>
          )}

          {/* Story Section */}
          <Box
            sx={{
              display: 'flex',
              overflowX: 'auto',
              gap: 2,
              pb: 2,
              scrollbarWidth: 'none',
              '&::-webkit-scrollbar': { display: 'none' },
              cursor: 'grab',
              mb: 2
            }}
          >
            {/* Updated Add Story Element */}
            <Box
              sx={{ position: 'relative', cursor: 'pointer' }}
              onClick={() => setOpenStoryCamera(true)}
            >
              <Avatar
                src={user?.profileImage}
                sx={{
                  width: 40,
                  height: 40,
                  border: '3px solid #ff4500'
                }}
              />
              <AddIcon
                sx={{
                  position: 'absolute',
                  bottom: -2,
                  right: -2,
                  backgroundColor: 'white',
                  borderRadius: '50%',
                  fontSize: 18,
                  color: '#ff4500'
                }}
              />
            </Box>
            {uniqueUserStories.map(({ userId, stories }, index) => {
              const storyUser = users.find(user => user.id === userId);
              if (!storyUser) return null;
              const hasSeenAllStories = stories.every(
                s => Array.isArray(s.views) && s.views.includes(user?._id)
              );
              return (
                <Avatar
                  key={index}
                  sx={{
                    width: 40,
                    height: 40,
                    cursor: 'pointer',
                    border: `3px solid ${hasSeenAllStories ? 'gray' : '#ff4500'}`
                  }}
                  src={storyUser.profileImage}
                  onClick={() => handleStoryClick(storyUser, stories)}
                />
              );
            })}
          </Box>

          {/* Posts Section */}
          <Box
            sx={{
              flex: 1,
              overflowY: 'auto',
              '&::-webkit-scrollbar': { display: 'none' },
              scrollbarWidth: 'none'
            }}
          >
            {posts.length > 0 ? (
              <Grid container spacing={3}>
                {posts.map((post, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Paper
                      sx={{
                        p: 3,
                        backgroundColor: 'transparent',
                        borderRadius: 2,
                        cursor: 'pointer'
                      }}
                      onClick={() => {
                        setSelectedPost(post);
                        setOpenPostModal(true);
                      }}
                    >
                      {post.postimg && (
                        <Box sx={{ mb: 2 }}>
                          <img
                            src={post.postimg}
                            alt="Post"
                            style={{ width: '100%', height: '150px', objectFit: 'cover' }}
                          />
                        </Box>
                      )}
                      <Typography variant="body2">{post.content}</Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                        <IconButton onClick={() => handleLike(post.postId)}>
                          <FavoriteIcon sx={{ color: likedPosts[post.postId] ? 'red' : 'gray' }} />
                        </IconButton>
                        <Typography variant="body2">{post.likes} Likes</Typography>
                      </Box>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Typography variant="body2" sx={{ textAlign: 'center', color: 'gray', fontStyle: 'italic' }}>
                No Posts Available
              </Typography>
            )}
          </Box>

          {/* Bottom Navigation - Fixed to Bottom */}
          <Box sx={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            width: '100%',
            display: 'flex',
            justifyContent: 'space-around',
            alignItems: 'center',
            p: 1,
            borderTop: '1px solid #ccc'
          }}>
            <IconButton onClick={() => handleProfile(user?._id)}>
              <Avatar src={user?.profileImage} sx={{ width: 50, height: 50, borderRadius: '50%' }} />
            </IconButton>

            <IconButton onClick={handleAddpost}
              sx={{
                bgcolor: '#ff4500',
                color: 'white',
                width: 50,
                height: 50,
                borderRadius: '50%',
                boxShadow: 3
              }}>
              <AddIcon sx={{ fontSize: 30 }} />
            </IconButton>

            <IconButton 
              onClick={handleHomeClick}
              sx={{ 
                backgroundColor: '#ff9800', 
                color: 'white', 
                borderRadius: '50%', 
                width: 50,             
                height: 50,            
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              <HomeIcon fontSize="large" />
            </IconButton>
          </Box>
        </Box>
      </Container>

      {/* Modal for Post Details */}
      <Modal open={openPostModal} onClose={() => setOpenPostModal(false)}>
        <Box sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: 600,
          maxHeight: 800,
          bgcolor: 'white',
          p: 3,
          borderRadius: 2,
          overflowY: 'auto'
        }}>
          {selectedPost && (
            <>
              {selectedPost.postimg && (
                <Box sx={{ mb: 2 }}>
                  <img 
                    src={selectedPost.postimg} 
                    alt="Post Detail" 
                    style={{ width: '100%', height: '300px', objectFit: 'cover' }} 
                  />
                </Box>
              )}
              <Typography variant="body1" sx={{ mb: 2 }}>
                {selectedPost.content}
              </Typography>
              {/* Options: Like, Comment, Share as icons */}
              <Box sx={{ display: 'flex', justifyContent: 'space-around' }}>
                <IconButton>
                  <FavoriteIcon />
                </IconButton>
                <IconButton>
                  <ChatIcon />
                </IconButton>
                <IconButton>
                  <AddIcon /> {/* Replace with ShareIcon if available */}
                </IconButton>
              </Box>
            </>
          )}
        </Box>
      </Modal>

      {/* Modal for Camera Capture */}
      <Modal open={openCamera} onClose={() => setOpenStoryCamera(false)}>
        <Box
          sx={{
            position: "absolute",
            marginTop: '50px',
            left: "50%",
            transform: "translateX(-50%)",
            width: 430,
            bgcolor: "white",
            boxShadow: 24,
            borderRadius: 2,
          }}
        >
          <CameraCapture onImageUpload={handleImageUpload} />
        </Box>
      </Modal>

      {/* Modal for Story View */}
      <Modal open={openStory} onClose={() => setOpenStory(false)}>
        <Box
          sx={{
            position: "absolute",
            left: "50%",
            transform: "translateX(-50%)",
            width: 430,
            height: 800,
            bgcolor: "black",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            borderRadius: 2,
            overflow: "hidden",
            mt: 6,
            position: "relative",
          }}
        >
          {userStories.length > 0 && (
            <img
              src={userStories[currentIndexStory].mediaUrl}
              alt={`Story ${currentIndexStory + 1}`}
              style={{
                width: 430,
                height: 800,
                objectFit: "cover",
                borderRadius: 10,
              }}
            />
          )}
          <IconButton
            onClick={() => setOpenStory(false)}
            sx={{
              position: "absolute",
              top: 10,
              right: 10,
              backgroundColor: "rgba(255, 255, 255, 0.5)",
              color: "white",
            }}
          >
            <CloseIcon />
          </IconButton>
          {currentIndexStory > 0 && (
            <IconButton
              onClick={handlePrevStory}
              sx={{
                position: "absolute",
                left: 10,
                top: "50%",
                transform: "translateY(-50%)",
                backgroundColor: "rgba(255, 255, 255, 0.3)",
                color: "white",
              }}
            >
              <ArrowBackIosNewIcon />
            </IconButton>
          )}
          {currentIndexStory < userStories.length - 1 && (
            <IconButton
              onClick={handleNextStory}
              sx={{
                position: "absolute",
                right: 10,
                top: "50%",
                transform: "translateY(-50%)",
                backgroundColor: "rgba(255, 255, 255, 0.3)",
                color: "white",
              }}
            >
              <ArrowForwardIosIcon />
            </IconButton>
          )}
        </Box>
      </Modal>
    </>
  );
};

export default UserHome;
